import { Box, ImageList, ImageListItem , Typography } from '@mui/material';
import React from 'react';
import { Link } from 'react-router-dom';

const section2 = () => {
    const itemData = [
        {
          img: "../../../../Imgs/photo4.jpg",
          rows: 1,
          cols: 2,
          offer: "40%",
          title: "Bedroom side Table",
        },
        {
          img: "../../../../Imgs/photo3.jpg",
          rows: 1,
          cols: 3,
          offer: "50%",
          title: "Sofa in White Room",
        },
        {
          img: "../../../../Imgs/photo2.jpg",
          rows: 2,
          cols: 4,
          offer: "50%",
          title: "Lambert & Fils laurent",
        },
        {
            img: "../../../../Imgs/photo1.jpg",
            rows: 1,
            cols: 3,
            offer: "25%",
            title: "Sapphire Chair in room",
          },
          {
            img: "../../../../Imgs/photo5.jpg",
            rows: 1,
            cols: 2,
            offer: "35%",
            title: "comfy chairs sink",
          },
      ];


  return (
    <>
     <Box sx={{ bgcolor: "#FFF", width: "75%" , margin: '50px auto'}}>
        <hr></hr>
        
        <hr></hr>
        <Typography sx={{fontWeight: '900', m : '20px 0px'}} variant='h4'>One Stop Custom and Factory <br/> Furniture Shopping</Typography>

      <Box sx={{}}>
        <ImageList
          sx={{ width: "100%", height: "auto", mx: "auto" }}
          gap={20}
          variant="quilted"
          cols={9}
          rowHeight={280}
        >
          {itemData.map((item) => (
            <ImageListItem
              key={item.title}
              cols={item.cols}
              rows={item.rows}
              sx={{
                borderRadius: "15px",
                backgroundImage: `url(${item.img})`,
                backgroundPosition: "center center",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundOrigin: "content-box",
                position : 'relative',
                textAlign: 'center',
              }}
            >
              <Box
                sx={{
                  position : 'absolute',
                  left : '10%',
                  bgcolor: "#fff",
                  borderRadius: "15px",
                  lineHeight: 2,
                  width: "80%",
                  margin: ' 30px 0',
                  overFlow :"hidden"
                }}
              >
               <Typography  sx={{margin: '10px' , fontWeight: '600',}}>{item.title}</Typography> 
               <Typography  sx={{ fontWeight: '600',fontSize:"12px" ,color:"gray"}}>Up to <span style={{color :"#AC8C5B", textDecoration: "underline"}}>{item.offer}</span> of item</Typography> 
               <Link 
                    to="/Shop"
                    className='Link'
                    style={{
                      textDecoration: "underline",
                      color: "gray",
                      fontSize: "12px",
                      fontWeight: '600',

                      "&:hover":{ color:"#AC8C5B", }
                    }}
                  >
                    Shop Now
                  </Link>
              </Box>
            </ImageListItem>
          ))}
        </ImageList>
      </Box>
      </Box>
    </>
  );
};

export default section2;
