import { Box, Typography } from '@mui/material';
import EastIcon from '@mui/icons-material/East';
import React from 'react';

const Shopdetails = () => {
    return (
        <Box >
            <Box sx={{width: "100%" , height:"150px", color:"#fff",display:"flex" , justifyContent:"center",alignItems: "center"}} className="DetailCover">
                <Typography variant='h4'>Shop Details</Typography>
                <Typography>Home   <EastIcon sx={{transform: "translateY(3px)", fontSize:"6px" }}/><span style={{color:"#ac8c5b"}}>   Details  </span></Typography>
            </Box>
            <Box sx={{width: "75%", display: "flex",mx: "20px"}}>
                <Box >
                    <img src="" alt="" />
                </Box>
                <Box sx={{border: "1px solid #000" , borderRight: "20px",mx: "20px"}}>
                    <Typography><span style={{color:"#ac8c5b"}}>Category: </span>Furniture ,Armchairs</Typography>
                </Box>
            </Box>
        </Box>
    );
}

export default Shopdetails;
