import { Box, Typography } from '@mui/material';
import EastIcon from '@mui/icons-material/East';
import React from 'react';

const Shopdetails = () => {
    return (
        <>
            <Box sx={{width: "100%" , height:"200px", color:"#fff",display:"flex",flexDirection:"column" , justifyContent:"center",alignItems: "center"}} className="DetailCover">
                <Typography variant='h4'>Shop Details</Typography>
                <Typography>Home   <EastIcon sx={{transform: "translateY(2px)", fontSize:"10px" }}/><span style={{color:"#ac8c5b"}}>   Details  </span></Typography>
            </Box>

        <Box sx={{display: "flex",justifyContent:"space-between", alignItems:"center", border:"2px red solid"}}>
            <Box sx={{border:"2px green solid", width: "75%", display: "flex",mx:"auto", my: "50px" ,justifyContent:"space-between",flexWrap:"wrap" }}>
                <Box width="45%" sx={{border: "1px solid gray", bgcolor:"#fefefe",borderRadius: "15px"}} >
                    <img style={{width:"100%"}} src="../../../Imgs/Productss.png" alt="" />
                </Box>
                <Box sx={{width:"45%" ,border: "1px solid #000" , borderRadius: "20px",padding:"30px"}}>
                    <Typography sx><span style={{color:"#ac8c5b"}}>Category: </span>Furniture ,Armchairs</Typography>
                </Box>
            </Box>
        </Box>
        </>
    );
}

export default Shopdetails;
