import { Box, ImageList, ImageListItem , Typography, useMediaQuery,
    useTheme} from '@mui/material';
import React from 'react';

const section2 = () => {
    const itemData = [
        {
          img: "../../../../Imgs/Design1.jpg",
          rows: 1,
          cols: 2,
          discount: "30% Discount",
          title: `New Stylish Decor Furniture`,
          deletedPrice: 400,
          price: 250,
        },
        {
          img: "../../../../Imgs/design3.jpg",
          rows: 1,
          cols: 3,
          discount: "Mega Offer 36% Off",
          title: "The Latest Collection of Furniture",
          deletedPrice: 350,
          price: 280,
        },
        {
          img: "../../../../Imgs/design2.jpg",
          rows: 2,
          cols: 4,
          discount: "Exclusive Offer 50% Off",
          title: "Farmaat Wooden Chaire",
          deletedPrice: 500,
          price: 250,
        },
        {
            img: "../../../../Imgs/Design1.jpg",
            rows: 1,
            cols: 3,
            discount: "30% Discount",
            title: `New Stylish Decor Furniture`,
            deletedPrice: 400,
            price: 250,
          },
          {
            img: "../../../../Imgs/design3.jpg",
            rows: 1,
            cols: 2,
            discount: "Mega Offer 36% Off",
            title: "The Latest Collection of Furniture",
            deletedPrice: 350,
            price: 280,
          },
      ];

      const theme = useTheme();
      const matchDownMd = useMediaQuery(theme.breakpoints.down("md"));

  return (
    <>
     <Box sx={{ bgcolor: "#FFF", width: "75%" }}>

        <Typography variant='h3'>One Stop Custom and Factory Furniture Shopping</Typography>

      <Box sx={{ padding: "16px", paddingBottom: "0px" }}>
        <ImageList
          sx={{ width: "75%", height: "auto", mx: "auto" }}
          gap={20}
          variant="quilted"
          cols={9}
          rowHeight={280}
        >
          {itemData.map((item) => (
            <ImageListItem
              key={item.title}
              cols={matchDownMd ? 9 : item.cols}
              rows={matchDownMd ? 1 : item.rows}
              sx={{
                borderRadius: "15px",
                backgroundImage: `url(${item.img})`,
                backgroundPosition: "center center",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundOrigin: "content-box",
              }}
            >
              <Box
                sx={{
                  color: "#fff",
                  my: "25px",
                  lineHeight: 2,
                  ml: "30px",
                  width: "70%",
                  marginY: item.cols === 3 ? "36px" : null,
                }}
              >
                
              </Box>
            </ImageListItem>
          ))}
        </ImageList>
      </Box>
      </Box>
    </>
  );
};

export default section2;
