import { Box, ImageList, ImageListItem , Typography, useTheme } from '@mui/material';
import React from 'react';
import { IsMobile } from '../../../ExternalMethods/matchDownMd';

const section2 = () => {
    const itemData = [
        {
          img: "../../../../Imgs/photo4.jpg",
          rows: 1,
          cols: 2,
          offer: "Up to 40% of item",
          title: "Bedroom side Table",
        },
        {
          img: "../../../../Imgs/photo3.jpg",
          rows: 1,
          cols: 3,
          offer: "Up to 50% of item",
          title: "The Latest Collection of Furniture",
        },
        {
          img: "../../../../Imgs/photo2.jpg",
          rows: 2,
          cols: 4,
          offer: "Up to 50% of item",
          title: "Farmaat Wooden Chaire",
        },
        {
            img: "../../../../Imgs/photo1.jpg",
            rows: 1,
            cols: 3,
            offer: "Up to 25% of item",
            title: `New Stylish Decor Furniture`,
          },
          {
            img: "../../../../Imgs/photo5.jpg",
            rows: 1,
            cols: 2,
            offer: "Up to 35% of item",
            title: "The Latest Collection of Furniture",
          },
      ];


  return (
    <>
     <Box sx={{ bgcolor: "#FFF", width: "75%" , margin: '50px auto'}}>
        <hr></hr>
        
        <hr></hr>
        <Typography sx={{fontWeight: '900', m : '20px 0px'}} variant='h4'>One Stop Custom and Factory <br/> Furniture Shopping</Typography>

      <Box sx={{}}>
        <ImageList
          sx={{ width: "100%", height: "auto", mx: "auto" }}
          gap={20}
          variant="quilted"
          cols={9}
          rowHeight={280}
        >
          {itemData.map((item) => (
            <ImageListItem
              key={item.title}
              cols={IsMo? 9:item.cols}
              rows={item.rows}
              sx={{
                borderRadius: "15px",
                backgroundImage: `url(${item.img})`,
                backgroundPosition: "center center",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundOrigin: "content-box",
                position : 'relative',
                textAlign: 'center',
              }}
            >
              <Box
                sx={{
                  position : 'absolute',
                  bgcolor: "#fff",
                  borderRadius: "15px",
                  mx: "auto",
                  lineHeight: 2,
                  ml: "30px",
                  width: "70%",
                }}
              >
               <Typography >{item.title}</Typography> 
              </Box>
            </ImageListItem>
          ))}
        </ImageList>
      </Box>
      </Box>
    </>
  );
};

export default section2;
