import { Box } from '@mui/material';
import React from 'react';

const section2 = () => {
    const itemData = [
        {
          img: "../../../../Imgs/Design1.jpg",
          rows: 2,
          cols: 3,
          discount: "30% Discount",
          title: `New Stylish Decor Furniture`,
          deletedPrice: 400,
          price: 250,
        },
        {
          img: "../../../../Imgs/design3.jpg",
          cols: 3,
          cols: 3,
          discount: "Mega Offer 36% Off",
          title: "The Latest Collection of Furniture",
          deletedPrice: 350,
          price: 280,
        },
        {
          img: "../../../../Imgs/design2.jpg",
          cols: 4,
          cols: 3,
          discount: "Exclusive Offer 50% Off",
          title: "Farmaat Wooden Chaire",
          deletedPrice: 500,
          price: 250,
        },
        {
            img: "../../../../Imgs/Design1.jpg",
            rows: 2,
            cols: 3,
            discount: "30% Discount",
            title: `New Stylish Decor Furniture`,
            deletedPrice: 400,
            price: 250,
          },
          {
            img: "../../../../Imgs/design3.jpg",
            cols: 2,
            discount: "Mega Offer 36% Off",
            title: "The Latest Collection of Furniture",
            deletedPrice: 350,
            price: 280,
          },
      ];

  return (
    <>
     <Box sx={{ bgcolor: "#FFF" }}>
      <Box sx={{ padding: "16px", paddingBottom: "0px" }}>
        <ImageList
          sx={{ width: "75%", height: "auto", mx: "auto" }}
          gap={20}
          variant="quilted"
          cols={9}
          rowHeight={200}
        >
          {itemData.map((item) => (
            <ImageListItem
              key={item.title}
              cols={matchDownMd ? 5 : item.cols}
              rows={matchDownMd ? 1 : item.rows}
              sx={{
                borderRadius: "15px",
                backgroundImage: `url(${item.img})`,
                backgroundPosition: "center center",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundOrigin: "content-box",
              }}
            >
              <Box
                sx={{
                  color: "#fff",
                  my: "25px",
                  lineHeight: 2,
                  ml: "30px",
                  width: "70%",
                  marginY: item.cols === 3 ? "36px" : null,
                }}
              >
                <Chip
                  label={item.discount}
                  sx={{
                    mb: item.cols === 3 ? "8px" : "4px",
                    borderRadius: "5px",
                    backgroundColor: "#FCC012",
                    color: "#000",
                  }}
                />
                <Typography
                  variant={item.cols === 3 ? "h3" : "h5"}
                  sx={{ fontSize: matchDownMd ? "20px" : null }}
                >
                  {item.title}
                </Typography>
                <Typography sx={{ fontSize: matchDownMd ? "12px" : "18px" }}>
                  <del style={{ color: "lightgray" }}>${item.deletedPrice}</del>{" "}
                  ${item.price}
                </Typography>
                {item.cols === 3 ? (
                  <Button
                    sx={{
                      fontSize: matchDownMd ? "10px" : "16px",
                      mt: matchDownMd ? "10px" : "50px",
                      backgroundColor: "#FCC012",
                      cursor: "pointer",
                      color: "black",
                      padding: "10px 16px",
                      borderRadius: "20px",
                      ":hover": {
                        color: "#FCC012",
                        outline: "1px solid #FCC012",
                      },
                    }}
                    onClick={() => {
                      Navigate("/Shop");
                    }}
                  >
                    Shop Now
                  </Button>
                ) : (
                  <Link
                    to="/Shop"
                    style={{
                      textDecoration: "underline",
                      color: "lightgray",
                      fontSize: matchDownMd ? "10px" : "16px",
                    }}
                  >
                    Shop Now
                  </Link>
                )}
              </Box>
            </ImageListItem>
          ))}
        </ImageList>
      </Box>
      </Box>
    </>
  );
};

export default section2;
