import {
    Box,
    Button,
    Chip,
    IconButton,
    Rating,
    Stack,
    Typography,
    useMediaQuery,
    useTheme,
  } from "@mui/material";
  import React, { useState } from "react";
  import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
  import FavoriteIcon from "@mui/icons-material/Favorite";
  import { Swiper, SwiperSlide } from "swiper/react";
  import "swiper/css";
  import "swiper/css/navigation";
  import "swiper/css/pagination";
  import "swiper/css/scrollbar";
  import { Navigation, Pagination, Scrollbar, A11y } from "swiper";
  
  
  const CategorySection = () => {
     const [fav, setfav] = useState(false);
     const [outlinefav, setoutlinefav] = useState(true);
     const theme = useTheme();
     const isSmallScreen = useMediaQuery(theme.breakpoints.down("md"));
     const num = isSmallScreen ? 1 : 4;
  return (
    <Box
    sx={{
      bgcolor: "#FFF",
      //p: "20px",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      height: "470px",
    }}
  >
    <Box sx={{ transform: "translateY(57px)", width: "75%" }}>
      <Typography
        sx={{ fontWeight: "900", width : "100%" }}
        variant={isSmallScreen ? "h5" : "h4"}
      >
        Popular Categories
      </Typography>
    </Box>

    <Box
      className="Cards-Container"
      sx={{
        display: "flex",
        flexDirection: "column",
        width: "72%",
        height: "410px",
        //padding: "10px",
      }}
    >
      <Swiper
        modules={[Navigation, Pagination, Scrollbar, A11y]}
        spaceBetween={5}
        slidesPerView={num}
        navigation
        style={{ width: "100%", height: "100%", padding: "30px 0" }}
      >
        <SwiperSlide style={{ display: "flex", alignItems: "end" , justifyContent : "center" }}>
          <Box
            style={{
              height: "290px",
              backgroundColor: "#F3F2EE",
              borderRadius: "15px",
              padding: "10px",
              border: '1px solid #ac8c5b',
              position: "relative",
              overflow: "hidden",
              cursor: "pointer",
            }}>

            <Box sx={{ width: "90%", height: "65%", mx: "auto" }}>
              <img
                style={{ width: "100%", height: "100%" }}
                src="../../../../Imgs/Productss.png"
                alt=""
              />
            </Box>
            <Box
              className="cardcontent"
              sx={{
                p: "5px",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                bgcolor: "#FFF",
                mx: "auto",
                width: "95%",
                height: "32%",
                borderRadius: "15px",
              }}
            >
              <Typography variant="h6">Chair</Typography>
              <Box sx={{ mt: "5px", display: "flex" }}>
                <Typography>15 products</Typography>
              </Box>

              <Button
                className="btn-tocart"
                sx={{ bgcolor: "#AC8C5B", display: "none" }}
                variant="contained"
              >
                add to cart
              </Button>
            </Box>
          </Box>
        </SwiperSlide>
      </Swiper>
    </Box>

    </Box>

  )
};
export default CategorySection ;
