import { Box, Chip, ImageList, ImageListItem, Typography, useMediaQuery, useTheme } from '@mui/material';
import React from 'react';

const Section1 = () => {
    const itemData = [
      {
        img: '../../../../Imgs/Design1.jpg',
        rows: 2,
        cols: 3,
        discount : "30% Discount",
        title: `New Stylish ${<br/>} Decor Furniture`,
        deletedPrice: 400,
        price : 250,
      },
      {
        img: '../../../../Imgs/design2.jpg',
        cols: 2,
        discount : "Mega Offer 36% Off",
        title: 'The Latest Collection of Furniture',
        deletedPrice: 350,
        price : 280,
      },
      {
        img: '../../../../Imgs/design3.jpg',
        cols: 2,
        discount : "Exclusive Offer 50% Off",
        title: 'Farmaat Wooden Chaire',
        deletedPrice: 500,
        price : 250,
      },
    ];

    // this hook used to make responsive from xs to md => cols={matchDownMd ? 5 : item.cols }
    const theme = useTheme()
    const matchDownMd = useMediaQuery(theme.breakpoints.down('md'));

    
    return (
      <>

<Box sx={{ padding: "16px", bgcolor : "#CECECA" }}>
        <ImageList
          sx={{ width: "75%", height: "auto" , mx: "auto" 
        }}
          gap = {20}
          variant="quilted"
          cols={5}
          rowHeight={200}
        >
            {itemData.map((item) => (
              <ImageListItem key={item.title} cols={matchDownMd ? 5 : item.cols }
              rows={matchDownMd ? 1 : item.rows } 
              sx={{borderRadius: '15px' , 
              backgroundImage: `url(${item.img})`,
              backgroundPosition: 'center center',
              backgroundSize: 'cover',
              backgroundRepeat: 'no-repeat',
              backgroundOrigin: 'content-box',
              
            }}>
                <Chip label={item.discount} 
                sx={{ml : '30px' , mt : '30px'}}
                color="warning" />
                <Typography variant='h5' sx={{color : '#fff' ,ml : '30px'}}>
                    {item.title}
                </Typography>


              </ImageListItem>
            ))}
        </ImageList>
        </Box>
      </>
    );
}

export default Section1;
